import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor to handle auth errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Auth API
export const authAPI = {
  login: (credentials) => api.post('/auth/login', credentials),
  register: (userData) => api.post('/auth/register', userData),
  verifyToken: () => api.post('/auth/verify'),
  getCurrentUser: () => api.get('/auth/me'),
};

// PDF API
export const pdfAPI = {
  upload: (file) => {
    const formData = new FormData();
    formData.append('pdf', file);
    return api.post('/pdf/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });
  },
  list: () => api.get('/pdf/list'),
  get: (uuid) => api.get(`/pdf/${uuid}`, { 
    responseType: 'arraybuffer',  // Changed from 'blob' to 'arraybuffer' for better compatibility
    headers: {
      'Accept': 'application/pdf'
    }
  }),
  delete: (uuid) => api.delete(`/pdf/${uuid}`),
  rename: (uuid, name) => api.patch(`/pdf/${uuid}/rename`, { name }),
  // Add debug method temporarily
  debug: (uuid) => api.get(`/pdf/debug/${uuid}`),
  // Add list all for debugging
  listAll: () => api.get('/pdf/list')
};

// Highlight API
export const highlightAPI = {
  create: (highlightData) => api.post('/highlights', highlightData),
  getByPDF: (pdfUuid) => api.get(`/highlights/pdf/${pdfUuid}`),
  update: (uuid, data) => api.patch(`/highlights/${uuid}`, data),
  delete: (uuid) => api.delete(`/highlights/${uuid}`),
  getAll: () => api.get('/highlights'),
};

export default api;
