import React, { useState, useRef, useCallback } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Document, Page, pdfjs } from 'react-pdf';
import toast from 'react-hot-toast';
import { pdfAPI, highlightAPI } from '../services/api';
import { ArrowLeft, ZoomIn, ZoomOut, ChevronLeft, ChevronRight, Highlighter } from 'lucide-react';
import { v4 as uuidv4 } from 'uuid';

// Set up PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const PDFViewer = () => {
  const { uuid } = useParams();
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState(1.0);
  const [isSelecting, setIsSelecting] = useState(false);
  const [selection, setSelection] = useState(null);
  const [highlights, setHighlights] = useState([]);
  const textLayerRef = useRef(null);
  const queryClient = useQueryClient();

  // Fetch PDF file as blob
  const { data: pdfBlob, isLoading: pdfLoading } = useQuery(
    ['pdf', uuid],
    () => pdfAPI.get(uuid),
    {
      select: (response) => new Blob([response.data], { type: 'application/pdf' }),
      enabled: !!uuid,
    }
  );

  // Fetch highlights for this PDF
  const { data: highlightsData } = useQuery(
    ['highlights', uuid],
    () => highlightAPI.getByPDF(uuid),
    {
      enabled: !!uuid,
      onSuccess: (response) => {
        setHighlights(response.data.highlights);
      },
    }
  );

  // Create highlight mutation
  const createHighlightMutation = useMutation(highlightAPI.create, {
    onSuccess: () => {
      queryClient.invalidateQueries(['highlights', uuid]);
      toast.success('Highlight created!');
    },
    onError: (error) => {
      toast.error(error.response?.data?.error || 'Failed to create highlight');
    },
  });

  // Delete highlight mutation
  const deleteHighlightMutation = useMutation(highlightAPI.delete, {
    onSuccess: () => {
      queryClient.invalidateQueries(['highlights', uuid]);
      toast.success('Highlight deleted!');
    },
    onError: (error) => {
      toast.error(error.response?.data?.error || 'Failed to delete highlight');
    },
  });

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  const handleTextSelection = useCallback(() => {
    if (!isSelecting) return;

    const selection = window.getSelection();
    if (selection.rangeCount > 0 && selection.toString().trim()) {
      const range = selection.getRangeAt(0);
      const rect = range.getBoundingClientRect();
      const pageElement = document.querySelector('.react-pdf__Page');
      const pageRect = pageElement.getBoundingClientRect();

      // Calculate relative position within the page
      const relativeRect = {
        left: (rect.left - pageRect.left) / pageRect.width,
        top: (rect.top - pageRect.top) / pageRect.height,
        right: (rect.right - pageRect.left) / pageRect.width,
        bottom: (rect.bottom - pageRect.top) / pageRect.height,
      };

      const highlightData = {
        pdfUuid: uuid,
        pageNumber,
        highlightedText: selection.toString().trim(),
        position: {
          x: relativeRect.left,
          y: relativeRect.top,
          width: relativeRect.right - relativeRect.left,
          height: relativeRect.bottom - relativeRect.top,
        },
        boundingBox: {
          left: relativeRect.left,
          top: relativeRect.top,
          right: relativeRect.right,
          bottom: relativeRect.bottom,
        },
      };

      createHighlightMutation.mutate(highlightData);
      selection.removeAllRanges();
    }
  }, [isSelecting, pageNumber, uuid, createHighlightMutation]);

  const handlePageClick = useCallback(
    (event) => {
      if (isSelecting) {
        // Small delay to allow text selection to complete
        setTimeout(handleTextSelection, 100);
      }
    },
    [isSelecting, handleTextSelection]
  );

  const renderHighlights = () => {
    const pageHighlights = highlights.filter(h => h.pageNumber === pageNumber);
    
    return pageHighlights.map((highlight) => (
      <div
        key={highlight.uuid}
        className="absolute pointer-events-auto cursor-pointer"
        style={{
          left: `${highlight.boundingBox.left * 100}%`,
          top: `${highlight.boundingBox.top * 100}%`,
          width: `${(highlight.boundingBox.right - highlight.boundingBox.left) * 100}%`,
          height: `${(highlight.boundingBox.bottom - highlight.boundingBox.top) * 100}%`,
          backgroundColor: highlight.color || '#ffff00',
          opacity: 0.3,
          mixBlendMode: 'multiply',
        }}
        onClick={(e) => {
          e.stopPropagation();
          if (window.confirm('Delete this highlight?')) {
            deleteHighlightMutation.mutate(highlight.uuid);
          }
        }}
        title={`"${highlight.highlightedText}" - Click to delete`}
      />
    ));
  };

  if (pdfLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        <span className="ml-3 text-lg">Loading PDF...</span>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen">
      {/* Header */}
      <div className="bg-white border-b px-4 py-3 flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <Link
            to="/dashboard"
            className="flex items-center text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="h-5 w-5 mr-1" />
            Back to Library
          </Link>
        </div>

        {/* Controls */}
        <div className="flex items-center space-x-4">
          {/* Highlight Toggle */}
          <button
            onClick={() => setIsSelecting(!isSelecting)}
            className={`flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors ${
              isSelecting
                ? 'bg-yellow-100 text-yellow-800 border border-yellow-300'
                : 'bg-gray-100 text-gray-700 border border-gray-300 hover:bg-gray-200'
            }`}
          >
            <Highlighter className="h-4 w-4 mr-2" />
            {isSelecting ? 'Highlighting On' : 'Enable Highlighting'}
          </button>

          {/* Zoom Controls */}
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setScale(Math.max(0.5, scale - 0.1))}
              className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded"
            >
              <ZoomOut className="h-4 w-4" />
            </button>
            <span className="text-sm text-gray-700 min-w-[60px] text-center">
              {Math.round(scale * 100)}%
            </span>
            <button
              onClick={() => setScale(Math.min(3.0, scale + 0.1))}
              className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded"
            >
              <ZoomIn className="h-4 w-4" />
            </button>
          </div>

          {/* Page Navigation */}
          {numPages && (
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setPageNumber(Math.max(1, pageNumber - 1))}
                disabled={pageNumber <= 1}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <ChevronLeft className="h-4 w-4" />
              </button>
              <span className="text-sm text-gray-700 min-w-[80px] text-center">
                {pageNumber} of {numPages}
              </span>
              <button
                onClick={() => setPageNumber(Math.min(numPages, pageNumber + 1))}
                disabled={pageNumber >= numPages}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <ChevronRight className="h-4 w-4" />
              </button>
            </div>
          )}
        </div>
      </div>

      {/* PDF Viewer */}
      <div className="flex-1 overflow-auto bg-gray-100 p-4">
        <div className="flex justify-center">
          <div className="relative">
            {pdfBlob && (
              <Document
                file={pdfBlob}
                onLoadSuccess={onDocumentLoadSuccess}
                className="shadow-lg"
              >
                <div className="relative" onClick={handlePageClick}>
                  <Page
                    pageNumber={pageNumber}
                    scale={scale}
                    renderTextLayer={true}
                    renderAnnotationLayer={false}
                  />
                  {/* Highlight overlay */}
                  <div className="absolute inset-0 pointer-events-none">
                    {renderHighlights()}
                  </div>
                </div>
              </Document>
            )}
          </div>
        </div>
      </div>

      {/* Instructions */}
      {isSelecting && (
        <div className="bg-yellow-50 border-t border-yellow-200 px-4 py-2">
          <p className="text-sm text-yellow-800">
            <strong>Highlighting Mode:</strong> Select text with your mouse to create highlights. 
            Click on existing highlights to delete them.
          </p>
        </div>
      )}
    </div>
  );
};

export default PDFViewer;
