import React, { useState, useRef, useCallback, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Document, Page, pdfjs } from 'react-pdf';
import toast from 'react-hot-toast';
import { pdfAPI, highlightAPI } from '../services/api';
import { v4 as uuidv4 } from 'uuid';

// Enhanced PDF.js worker setup with error handling
const setupPDFWorker = () => {
  if (!pdfjs.GlobalWorkerOptions.workerSrc) {
    const workerUrls = [
      `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`,
      `https://unpkg.com/pdfjs-dist@${pdfjs.version}/build/pdf.worker.min.js`,
    ];
    pdfjs.GlobalWorkerOptions.workerSrc = workerUrls[0];
  }
};

setupPDFWorker();

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('PDF Viewer Error:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="text-red-500 text-xl mb-4">PDF Viewer Error</div>
            <p className="text-gray-600 mb-4">{this.state.error?.message}</p>
            <button
              onClick={() => this.setState({ hasError: false, error: null })}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Try Again
            </button>
          </div>
        </div>
      );
    }
    return this.props.children;
  }
}

const PDFViewer = () => {
  const { uuid } = useParams();
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState(1.0);
  const [isSelecting, setIsSelecting] = useState(false);
  const [highlights, setHighlights] = useState([]);
  const [isCreatingHighlight, setIsCreatingHighlight] = useState(false);
  const [pageSize, setPageSize] = useState({ width: 0, height: 0 });
  const [pdfDocument, setPdfDocument] = useState(null);
  const [debugInfo, setDebugInfo] = useState('');
  const [pdfError, setPdfError] = useState(null);
  
  const pageContainerRef = useRef(null);
  const documentRef = useRef(null);
  const queryClient = useQueryClient();

  // Debug query - REMOVE AFTER FIXING
  const { data: debugData } = useQuery(
    ['pdfDebug', uuid],
    () => pdfAPI.debug(uuid),
    {
      enabled: !!uuid,
      onSuccess: (response) => {
        console.log('ðŸ” Debug Data:', response.data);
        setDebugInfo(`Debug: ${JSON.stringify(response.data, null, 2)}`);
      },
      onError: (error) => {
        console.error('Debug failed:', error);
      }
    }
  );

  // Enhanced PDF data processing
  const processPdfData = useCallback(async (rawData) => {
    if (!rawData) {
      setDebugInfo('No raw PDF data');
      return null;
    }

    try {
      console.log('ðŸ”„ Processing PDF data...');
      console.log('Data type:', typeof rawData);
      console.log('Data constructor:', rawData.constructor.name);
      console.log('Data size:', rawData.byteLength || rawData.length || 'unknown');
      
      setDebugInfo('Processing PDF data...');

      let uint8Array;

      // Convert ArrayBuffer to Uint8Array (from responseType: 'arraybuffer')
      if (rawData instanceof ArrayBuffer) {
        uint8Array = new Uint8Array(rawData);
        console.log('âœ… Converted ArrayBuffer to Uint8Array');
      } else if (rawData instanceof Uint8Array) {
        uint8Array = rawData;
        console.log('âœ… Data is already Uint8Array');
      } else if (rawData instanceof Blob) {
        const arrayBuffer = await rawData.arrayBuffer();
        uint8Array = new Uint8Array(arrayBuffer);
        console.log('âœ… Converted Blob to Uint8Array');
      } else if (typeof rawData === 'string') {
        // Try base64 decode first
        try {
          const binaryString = atob(rawData);
          uint8Array = new Uint8Array(binaryString.length);
          for (let i = 0; i < binaryString.length; i++) {
            uint8Array[i] = binaryString.charCodeAt(i);
          }
          console.log('âœ… Converted base64 string to Uint8Array');
        } catch (e) {
          // If not base64, convert string directly
          const encoder = new TextEncoder();
          uint8Array = encoder.encode(rawData);
          console.log('âœ… Converted string to Uint8Array');
        }
      } else {
        throw new Error('Unsupported data type: ' + typeof rawData);
      }

      if (uint8Array.length === 0) {
        throw new Error('PDF data is empty');
      }

      // Validate PDF header
      const header = String.fromCharCode(...uint8Array.slice(0, 4));
      if (!header.startsWith('%PDF')) {
        throw new Error(`Invalid PDF header: ${header}. Expected %PDF`);
      }

      console.log('âœ… PDF validation successful:', {
        size: uint8Array.length,
        header: header
      });

      setDebugInfo(`PDF processed successfully (${uint8Array.length} bytes)`);
      return uint8Array;

    } catch (error) {
      console.error('âŒ Error processing PDF data:', error);
      setDebugInfo(`Processing error: ${error.message}`);
      setPdfError(error);
      throw error;
    }
  }, []);

  // Fetch PDF file with better error handling
  const { isLoading: pdfLoading, error: fetchError, data: rawPdfData } = useQuery(
    ['pdf', uuid],
    async () => {
      console.log('ðŸ”„ Fetching PDF with UUID:', uuid);
      try {
        const response = await pdfAPI.get(uuid);
        console.log('âœ… PDF API response received');
        console.log('Response data type:', typeof response.data);
        console.log('Response data constructor:', response.data.constructor.name);
        console.log('Response headers:', response.headers);
        return response.data;
      } catch (error) {
        console.error('âŒ PDF fetch error details:', {
          message: error.message,
          status: error.response?.status,
          statusText: error.response?.statusText,
          data: error.response?.data
        });
        setDebugInfo(`Fetch Error: ${error.message} (${error.response?.status})`);
        throw error;
      }
    },
    {
      enabled: !!uuid,
      retry: (failureCount, error) => {
        // Don't retry on 404s
        if (error.response?.status === 404) return false;
        return failureCount < 2;
      },
      onError: (error) => {
        console.error('Query error:', error);
        toast.error('Failed to load PDF: ' + (error.response?.data?.error || error.message));
      }
    }
  );

  // Process PDF data when it changes
  useEffect(() => {
    if (!rawPdfData) return;

    const loadPDF = async () => {
      try {
        setPdfError(null);
        const processedData = await processPdfData(rawPdfData);
        if (processedData) {
          setPdfDocument(processedData);
        }
      } catch (error) {
        console.error('Failed to process PDF:', error);
        toast.error('Failed to process PDF: ' + error.message);
      }
    };

    loadPDF();
  }, [rawPdfData, processPdfData]);

  // Rest of your component methods remain the same...
  // (onDocumentLoadSuccess, onPageLoadSuccess, etc.)

  const onDocumentLoadSuccess = (pdf) => {
    console.log('âœ… PDF document loaded successfully:', {
      numPages: pdf.numPages,
      fingerprint: pdf.fingerprint
    });
    
    documentRef.current = pdf;
    setNumPages(pdf.numPages);
    setDebugInfo(`Document loaded: ${pdf.numPages} pages`);
    toast.success(`PDF loaded successfully (${pdf.numPages} pages)`);
    setPdfError(null);
  };

  // ... (include all your other methods here - onDocumentLoadError, onPageLoadSuccess, etc.)
  // I'll skip them for brevity but they should remain the same

  if (pdfLoading) {
    return (
      <div className="flex flex-col h-screen">
        <div className="bg-white border-b px-4 py-3 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Link to="/dashboard" className="flex items-center text-gray-600 hover:text-gray-900">
              <svg className="h-5 w-5 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
              Back to Library
            </Link>
          </div>
        </div>
        
        <div className="flex-1 flex items-center justify-center bg-gray-100 p-8">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-700">Loading PDF document...</p>
            <p className="text-sm text-gray-500 mt-2">UUID: {uuid}</p>
            <p className="text-sm text-gray-500 mt-1">Debug: {debugInfo}</p>
            {debugData && (
              <div className="mt-4 p-4 bg-gray-200 rounded text-xs text-left max-w-md">
                <pre>{JSON.stringify(debugData.data, null, 2)}</pre>
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }

  if (fetchError || pdfError) {
    const error = fetchError || pdfError;
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center max-w-2xl">
          <div className="text-red-500 text-xl mb-4">Failed to load PDF</div>
          <p className="text-gray-600 mb-4">{error.message}</p>
          {error.response?.status && (
            <p className="text-sm text-gray-500 mb-2">Status: {error.response.status}</p>
          )}
          <p className="text-sm text-gray-500 mb-4">UUID: {uuid}</p>
          <p className="text-sm text-gray-500 mb-4">Debug: {debugInfo}</p>
          
          {debugData && (
            <div className="mb-4 p-4 bg-gray-100 rounded text-xs text-left">
              <h4 className="font-bold mb-2">Debug Information:</h4>
              <pre>{JSON.stringify(debugData.data, null, 2)}</pre>
            </div>
          )}
          
          <div className="space-x-4">
            <button
              onClick={() => window.location.reload()}
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Reload Page
            </button>
            <Link 
              to="/dashboard" 
              className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700"
            >
              Back to Dashboard
            </Link>
          </div>
        </div>
      </div>
    );
  }

  // Rest of your render method...
  return (
    <ErrorBoundary>
      <div className="flex flex-col h-screen">
        {/* Your existing render code here */}
        <div className="bg-gray-100 border-t px-4 py-2 text-xs text-gray-600">
          <div className="flex justify-between">
            <span>UUID: {uuid} | PDF: {pdfDocument ? 'Loaded' : 'Not loaded'}</span>
            <span>Debug: {debugInfo}</span>
          </div>
        </div>
      </div>
    </ErrorBoundary>
  );
};

export default PDFViewer;
