import { useState, useEffect } from 'react';
import { cloudStorageAPI } from '../services/api';

export const useGoogleAuth = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [accessToken, setAccessToken] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Check if user has stored tokens
  useEffect(() => {
    const storedToken = localStorage.getItem('google_access_token');
    const tokenExpiry = localStorage.getItem('google_token_expiry');
    
    console.log('üîç Checking stored tokens:', { 
      hasToken: !!storedToken, 
      hasExpiry: !!tokenExpiry,
      expiry: tokenExpiry ? new Date(parseInt(tokenExpiry)) : null
    });
    
    if (storedToken && tokenExpiry) {
      const now = new Date().getTime();
      if (now < parseInt(tokenExpiry)) {
        console.log('‚úÖ Using stored valid token');
        setAccessToken(storedToken);
        setIsAuthenticated(true);
      } else {
        console.log('‚è∞ Stored token expired, clearing...');
        localStorage.removeItem('google_access_token');
        localStorage.removeItem('google_token_expiry');
      }
    }
  }, []);

  // In useGoogleAuth.js, update the authenticate function
const authenticate = async () => {
  try {
    setIsLoading(true);
    setError(null);

    const response = await cloudStorageAPI.getAuthUrl();
    const authUrl = response.data.authUrl;
    
    console.log('üîó Opening auth URL:', authUrl);

    const authPromise = new Promise((resolve, reject) => {
      const popup = window.open(
        authUrl,
        'google-auth',
        'width=600,height=700,scrollbars=yes,resizable=yes,left=' + 
        Math.round(window.screen.width / 2 - 300) + ',top=' + 
        Math.round(window.screen.height / 2 - 350)
      );

      if (!popup) {
        reject(new Error('Popup blocked. Please allow popups for this site.'));
        return;
      }

      // Check popup URL for errors
      const checkPopup = setInterval(() => {
        try {
          if (popup.closed) {
            clearInterval(checkPopup);
            return;
          }
          
          // Try to read the URL for error detection
          const currentUrl = popup.location.href;
          console.log('üîç Popup URL changed:', currentUrl);
          
          // Check for error in URL
          if (currentUrl.includes('error=')) {
            clearInterval(checkPopup);
            const urlParams = new URLSearchParams(currentUrl.split('?')[1]);
            const error = urlParams.get('error');
            const errorDescription = urlParams.get('error_description');
            popup.close();
            reject(new Error(`OAuth Error: ${error} - ${errorDescription}`));
          }
        } catch (e) {
          // Cross-origin error is expected when on Google's domain
          if (!e.message.includes('cross-origin')) {
            console.log('Popup check error:', e.message);
          }
        }
      }, 1000);

      const messageHandler = (event) => {
        if (event.origin !== window.location.origin) return;

        clearInterval(checkPopup);

        if (event.data.type === 'GOOGLE_AUTH_SUCCESS') {
          window.removeEventListener('message', messageHandler);
          resolve({
            accessToken: event.data.accessToken,
            expiry: event.data.expiry
          });
        } else if (event.data.type === 'GOOGLE_AUTH_ERROR') {
          window.removeEventListener('message', messageHandler);
          reject(new Error(event.data.error || 'Authentication failed'));
        }
      };

      window.addEventListener('message', messageHandler);

      const checkClosed = setInterval(() => {
        if (popup.closed) {
          clearInterval(checkClosed);
          clearInterval(checkPopup);
          window.removeEventListener('message', messageHandler);
          reject(new Error('Authentication cancelled by user'));
        }
      }, 1000);

      setTimeout(() => {
        clearInterval(checkClosed);
        clearInterval(checkPopup);
        window.removeEventListener('message', messageHandler);
        if (!popup.closed) {
          popup.close();
        }
        reject(new Error('Authentication timeout'));
      }, 300000);
    });

    const { accessToken, expiry } = await authPromise;
    
    localStorage.setItem('google_access_token', accessToken);
    localStorage.setItem('google_token_expiry', expiry.toString());
    
    setAccessToken(accessToken);
    setIsAuthenticated(true);

  } catch (error) {
    console.error('‚ùå Auth error details:', error);
    setError(error.message || 'Authentication failed');
  } finally {
    setIsLoading(false);
  }
};

  const logout = () => {
    console.log('üö™ Logging out...');
    localStorage.removeItem('google_access_token');
    localStorage.removeItem('google_token_expiry');
    setAccessToken(null);
    setIsAuthenticated(false);
    setError(null);
  };

  return {
    isAuthenticated,
    accessToken,
    isLoading,
    error,
    authenticate,
    logout
  };
};
