import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import SearchBar from '../components/SearchBar';
import SearchResults from '../components/SearchResults';
import { useSearch } from '../contexts/SearchContext';

const Search = () => {
  const navigate = useNavigate();
  const { clearSearch } = useSearch();

  useEffect(() => {
    // Clear search when component mounts
    return () => {
      // Optional: clear search when leaving page
    };
  }, []);

  const handleResultClick = (result) => {
    // Navigate to PDF with page and highlight if available
    const url = `/pdf/${result.pdfUuid}?page=${result.pageNumber}`;
    if (result.highlightId) {
      navigate(`${url}&highlight=${result.highlightId}`);
    } else {
      navigate(url);
    }
  };

  return (
      <div className="max-w-4xl mx-auto py-6">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Search PDFs and Annotations
          </h1>
          <p className="text-gray-600">
            Find content across all your PDFs and highlights
          </p>
        </div>

        <div className="space-y-6">
          <SearchBar onResultClick={handleResultClick} />
          <SearchResults onResultClick={handleResultClick} />
        </div>
      </div>
  );
};

export default Search;
