import React, { useState, useRef, useCallback, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery, useMutation, useQueryClient } from 'react-query';
import { Document, Page, pdfjs } from 'react-pdf';
import toast from 'react-hot-toast';
import { pdfAPI, highlightAPI } from '../services/api';
import { v4 as uuidv4 } from 'uuid';

// Set up PDF.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

const PDFViewer = () => {
  const { uuid } = useParams();
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);
  const [scale, setScale] = useState(1.0);
  const [isSelecting, setIsSelecting] = useState(false);
  const [highlights, setHighlights] = useState([]);
  const [isCreatingHighlight, setIsCreatingHighlight] = useState(false);
  const [pdfUrl, setPdfUrl] = useState(null);
  const [debugInfo, setDebugInfo] = useState('');
  const pageContainerRef = useRef(null);
  const blobUrlRef = useRef(null);
  const queryClient = useQueryClient();

  // Fetch PDF file with better error handling and debugging
  const { isLoading: pdfLoading, error: pdfError, data: rawPdfData } = useQuery(
    ['pdf', uuid],
    async () => {
      console.log('Fetching PDF with UUID:', uuid);
      const response = await pdfAPI.get(uuid);
      console.log('PDF API response received');
      return response.data;
    },
    {
      enabled: !!uuid,
      retry: 2,
      onError: (error) => {
        console.error('PDF fetch error:', error);
        setDebugInfo(`Error: ${error.message}`);
        toast.error('Failed to load PDF: ' + error.message);
      }
    }
  );

  // Process PDF data when it changes
  useEffect(() => {
    if (!rawPdfData) {
      setDebugInfo('No raw PDF data');
      return;
    }

    const processPdfData = async () => {
      try {
        console.log('Processing PDF data...');
        console.log('Raw data type:', typeof rawPdfData);
        console.log('Raw data constructor:', rawPdfData.constructor.name);
        console.log('Raw data length/size:', rawPdfData.length || rawPdfData.size || 'unknown');

        let pdfBlob;
        
        if (rawPdfData instanceof Blob) {
          console.log('Data is already a Blob');
          pdfBlob = rawPdfData;
          setDebugInfo('Blob used directly');
        } else if (rawPdfData instanceof ArrayBuffer) {
          console.log('Data is ArrayBuffer, converting to Blob');
          pdfBlob = new Blob([rawPdfData], { type: 'application/pdf' });
          setDebugInfo('ArrayBuffer converted to Blob');
        } else if (rawPdfData instanceof Uint8Array) {
          console.log('Data is Uint8Array, converting to Blob');
          pdfBlob = new Blob([rawPdfData], { type: 'application/pdf' });
          setDebugInfo('Uint8Array converted to Blob');
        } else if (typeof rawPdfData === 'string') {
          console.log('Data is string, attempting base64 decode');
          try {
            const binaryString = atob(rawPdfData);
            const bytes = new Uint8Array(binaryString.length);
            for (let i = 0; i < binaryString.length; i++) {
              bytes[i] = binaryString.charCodeAt(i);
            }
            pdfBlob = new Blob([bytes], { type: 'application/pdf' });
            setDebugInfo('Base64 string converted to Blob');
          } catch (e) {
            // If not base64, treat as direct string
            pdfBlob = new Blob([rawPdfData], { type: 'application/pdf' });
            setDebugInfo('String converted to Blob');
          }
        } else {
          console.log('Unknown data type, creating blob');
          pdfBlob = new Blob([rawPdfData], { type: 'application/pdf' });
          setDebugInfo('Unknown type converted to Blob');
        }
        
        if (pdfBlob.size === 0) {
          throw new Error('PDF blob is empty');
        }

        // Clean up previous blob URL
        if (blobUrlRef.current) {
          URL.revokeObjectURL(blobUrlRef.current);
        }

        // Create a blob URL
        const blobUrl = URL.createObjectURL(pdfBlob);
        blobUrlRef.current = blobUrl;
        
        console.log('Blob URL created:', blobUrl);
        console.log('Blob size:', pdfBlob.size);
        
        // Verify it's a PDF by checking the first few bytes
        const reader = new FileReader();
        reader.onload = function(e) {
          const arrayBuffer = e.target.result;
          const uint8Array = new Uint8Array(arrayBuffer, 0, 10);
          const pdfHeader = String.fromCharCode(...uint8Array.slice(0, 4));
          console.log('PDF header check:', pdfHeader);
          
          if (!pdfHeader.startsWith('%PDF')) {
            console.warn('Data does not appear to be a PDF file. Header:', pdfHeader);
            setDebugInfo(`Warning: Invalid PDF header: ${pdfHeader}`);
          }
        };
        reader.readAsArrayBuffer(pdfBlob.slice(0, 10));
        
        setPdfUrl(blobUrl);
        setDebugInfo(`PDF loaded successfully (${pdfBlob.size} bytes)`);
        
      } catch (error) {
        console.error('Error processing PDF data:', error);
        setDebugInfo(`Processing error: ${error.message}`);
        toast.error('Failed to process PDF data: ' + error.message);
      }
    };

    processPdfData();

    // Cleanup function
    return () => {
      if (blobUrlRef.current) {
        URL.revokeObjectURL(blobUrlRef.current);
        blobUrlRef.current = null;
      }
    };
  }, [rawPdfData]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (blobUrlRef.current) {
        URL.revokeObjectURL(blobUrlRef.current);
      }
    };
  }, []);

  // Fetch highlights for this PDF
  const { data: highlightsData } = useQuery(
    ['highlights', uuid],
    () => highlightAPI.getByPDF(uuid),
    {
      enabled: !!uuid,
      onSuccess: (response) => {
        setHighlights(response.data.highlights || []);
      },
      onError: (error) => {
        console.error('Failed to fetch highlights:', error);
        toast.error('Failed to load highlights');
      },
    }
  );

  // Create highlight mutation
  const createHighlightMutation = useMutation(highlightAPI.create, {
    onSuccess: () => {
      queryClient.invalidateQueries(['highlights', uuid]);
      toast.success('Highlight created!');
      setIsCreatingHighlight(false);
    },
    onError: (error) => {
      console.error('Failed to create highlight:', error);
      toast.error(error.response?.data?.error || 'Failed to create highlight');
      setIsCreatingHighlight(false);
    },
  });

  // Delete highlight mutation
  const deleteHighlightMutation = useMutation(highlightAPI.delete, {
    onSuccess: () => {
      queryClient.invalidateQueries(['highlights', uuid]);
      toast.success('Highlight deleted!');
    },
    onError: (error) => {
      toast.error(error.response?.data?.error || 'Failed to delete highlight');
    },
  });

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
    console.log('PDF document loaded with', numPages, 'pages');
    toast.success(`PDF loaded successfully (${numPages} pages)`);
  };

  const onDocumentLoadError = (error) => {
    console.error('PDF Document load error:', error);
    setDebugInfo(`Document load error: ${error.message}`);
    toast.error('Failed to load PDF document: ' + error.message);
  };

  const onPageLoadSuccess = (page) => {
    const { width, height } = page.originalWidth 
      ? { width: page.originalWidth, height: page.originalHeight }
      : { width: page.width, height: page.height };
    
    console.log('Page loaded:', { width: width * scale, height: height * scale, scale });
  };

  // Get the correct page container element
  const getPageContainer = () => {
    if (!pageContainerRef.current) return null;
    const pageDiv = pageContainerRef.current.querySelector('.react-pdf__Page');
    return pageDiv;
  };

  // Enhanced text selection handler
  const handleTextSelection = useCallback(async () => {
    if (!isSelecting || isCreatingHighlight) return;

    const selection = window.getSelection();
    if (!selection.rangeCount || !selection.toString().trim()) return;

    setIsCreatingHighlight(true);

    try {
      const range = selection.getRangeAt(0);
      const selectedText = selection.toString().trim();
      
      const pageContainer = getPageContainer();
      if (!pageContainer) {
        throw new Error('Page container not found');
      }

      const canvas = pageContainer.querySelector('canvas');
      if (!canvas) {
        throw new Error('Canvas not found');
      }

      const pageRect = canvas.getBoundingClientRect();
      const rangeRect = range.getBoundingClientRect();

      const relativeRect = {
        left: Math.max(0, (rangeRect.left - pageRect.left) / pageRect.width),
        top: Math.max(0, (rangeRect.top - pageRect.top) / pageRect.height),
        right: Math.min(1, (rangeRect.right - pageRect.left) / pageRect.width),
        bottom: Math.min(1, (rangeRect.bottom - pageRect.top) / pageRect.height),
      };

      const tolerance = 0.05;
      if (relativeRect.left < -tolerance || relativeRect.top < -tolerance || 
          relativeRect.right > (1 + tolerance) || relativeRect.bottom > (1 + tolerance)) {
        throw new Error('Selection is outside page bounds');
      }

      const boundedRect = {
        left: Math.max(0, Math.min(1, relativeRect.left)),
        top: Math.max(0, Math.min(1, relativeRect.top)),
        right: Math.max(0, Math.min(1, relativeRect.right)),
        bottom: Math.max(0, Math.min(1, relativeRect.bottom)),
      };

      const highlightData = {
        pdfUuid: uuid,
        pageNumber,
        highlightedText: selectedText,
        position: {
          x: boundedRect.left,
          y: boundedRect.top,
          width: boundedRect.right - boundedRect.left,
          height: boundedRect.bottom - boundedRect.top,
        },
        boundingBox: {
          left: boundedRect.left,
          top: boundedRect.top,
          right: boundedRect.right,
          bottom: boundedRect.bottom,
        },
      };

      console.log('Creating highlight:', highlightData);
      await createHighlightMutation.mutateAsync(highlightData);
      
    } catch (error) {
      console.error('Error creating highlight:', error);
      toast.error('Failed to create highlight: ' + error.message);
      setIsCreatingHighlight(false);
    } finally {
      selection.removeAllRanges();
    }
  }, [isSelecting, pageNumber, uuid, createHighlightMutation, isCreatingHighlight]);

  const handleMouseUp = useCallback((event) => {
    if (!isSelecting) return;
    
    const pageContainer = getPageContainer();
    if (!pageContainer) return;

    const canvas = pageContainer.querySelector('canvas');
    if (!canvas) return;

    const canvasRect = canvas.getBoundingClientRect();
    const { clientX, clientY } = event;

    if (clientX >= canvasRect.left && clientX <= canvasRect.right &&
        clientY >= canvasRect.top && clientY <= canvasRect.bottom) {
      
      setTimeout(() => {
        const selection = window.getSelection();
        if (selection.toString().trim()) {
          handleTextSelection();
        }
      }, 100);
    }
  }, [isSelecting, handleTextSelection]);

  useEffect(() => {
    const pageContainer = getPageContainer();
    if (!pageContainer) return;

    if (isSelecting) {
      document.addEventListener('mouseup', handleMouseUp);
      pageContainer.style.userSelect = 'text';
      pageContainer.style.cursor = 'text';
    } else {
      document.removeEventListener('mouseup', handleMouseUp);
      pageContainer.style.userSelect = 'none';
      pageContainer.style.cursor = 'default';
    }

    return () => {
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isSelecting, handleMouseUp, pageNumber]);

  const renderHighlights = useCallback(() => {
    const pageHighlights = highlights.filter(h => h.pageNumber === pageNumber);
    
    if (pageHighlights.length === 0) return null;
    
    return pageHighlights.map((highlight) => (
      <div
        key={highlight.uuid}
        className="pdf-highlight absolute cursor-pointer"
        style={{
          left: `${highlight.boundingBox.left * 100}%`,
          top: `${highlight.boundingBox.top * 100}%`,
          width: `${(highlight.boundingBox.right - highlight.boundingBox.left) * 100}%`,
          height: `${(highlight.boundingBox.bottom - highlight.boundingBox.top) * 100}%`,
          backgroundColor: highlight.color || '#ffff00',
          opacity: 0.4,
          mixBlendMode: 'multiply',
          pointerEvents: 'auto',
          zIndex: 10,
          border: '1px solid rgba(255, 255, 0, 0.6)',
          borderRadius: '2px',
        }}
        onClick={(e) => {
          e.stopPropagation();
          e.preventDefault();
          if (window.confirm(`Delete highlight: "${highlight.highlightedText.substring(0, 50)}${highlight.highlightedText.length > 50 ? '...' : ''}"?`)) {
            deleteHighlightMutation.mutate(highlight.uuid);
          }
        }}
        title={`"${highlight.highlightedText}" - Click to delete`}
      />
    ));
  }, [highlights, pageNumber, deleteHighlightMutation]);

  if (pdfLoading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <div className="mt-3 text-lg">Loading PDF...</div>
          <div className="mt-2 text-sm text-gray-600">UUID: {uuid}</div>
        </div>
      </div>
    );
  }

  if (pdfError) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-center">
          <div className="text-red-500 text-xl mb-4">Failed to load PDF</div>
          <p className="text-gray-600 mb-4">{pdfError.message}</p>
          <p className="text-sm text-gray-500 mb-4">UUID: {uuid}</p>
          <p className="text-sm text-gray-500 mb-4">Debug: {debugInfo}</p>
          <Link to="/dashboard" className="text-blue-600 hover:underline">
            Back to Dashboard
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="flex flex-col h-screen">
      {/* Header */}
      <div className="bg-white border-b px-4 py-3 flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <Link
            to="/dashboard"
            className="flex items-center text-gray-600 hover:text-gray-900"
          >
            <svg className="h-5 w-5 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
            Back to Library
          </Link>
        </div>

        {/* Controls */}
        <div className="flex items-center space-x-4">
          {/* Highlight Toggle */}
          <button
            onClick={() => setIsSelecting(!isSelecting)}
            disabled={isCreatingHighlight || !pdfUrl}
            className={`flex items-center px-3 py-2 rounded-md text-sm font-medium transition-colors disabled:opacity-50 ${
              isSelecting
                ? 'bg-yellow-100 text-yellow-800 border border-yellow-300'
                : 'bg-gray-100 text-gray-700 border border-gray-300 hover:bg-gray-200'
            }`}
          >
            <svg className="h-4 w-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 4V2a1 1 0 011-1h8a1 1 0 011 1v2h4a1 1 0 110 2h-1v10a2 2 0 01-2 2H6a2 2 0 01-2-2V6H3a1 1 0 110-2h4zM6 6v10h12V6H6zm3 3h6v2H9V9z" />
            </svg>
            {isCreatingHighlight ? 'Creating...' : (isSelecting ? 'Highlighting On' : 'Enable Highlighting')}
          </button>

          {/* Zoom Controls */}
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setScale(Math.max(0.5, scale - 0.1))}
              className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded"
              disabled={!pdfUrl}
            >
              <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM13 10H7" />
              </svg>
            </button>
            <span className="text-sm text-gray-700 min-w-[60px] text-center">
              {Math.round(scale * 100)}%
            </span>
            <button
              onClick={() => setScale(Math.min(3.0, scale + 0.1))}
              className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded"
              disabled={!pdfUrl}
            >
              <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0zM15 10h-3m0 0H9m3 0v3m0-3V7" />
              </svg>
            </button>
          </div>

          {/* Page Navigation */}
          {numPages && (
            <div className="flex items-center space-x-2">
              <button
                onClick={() => setPageNumber(Math.max(1, pageNumber - 1))}
                disabled={pageNumber <= 1}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
              <span className="text-sm text-gray-700 min-w-[80px] text-center">
                {pageNumber} of {numPages}
              </span>
              <button
                onClick={() => setPageNumber(Math.min(numPages, pageNumber + 1))}
                disabled={pageNumber >= numPages}
                className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded disabled:opacity-50 disabled:cursor-not-allowed"
              >
                <svg className="h-4 w-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            </div>
          )}
        </div>
      </div>

      {/* PDF Viewer */}
      <div className="flex-1 overflow-auto bg-gray-100 p-4">
        <div className="flex justify-center">
          <div 
            className={`relative ${isSelecting ? 'highlighting-mode' : ''}`} 
            ref={pageContainerRef}
          >
            {pdfUrl ? (
              <Document
                file={pdfUrl}
                onLoadSuccess={onDocumentLoadSuccess}
                onLoadError={onDocumentLoadError}
                loading={
                  <div className="flex items-center justify-center p-8">
                    <div className="loading-spinner w-8 h-8"></div>
                    <span className="ml-3">Loading document...</span>
                  </div>
                }
                error={
                  <div className="flex items-center justify-center p-8 text-red-600">
                    <div className="text-center">
                      <svg className="w-6 h-6 mx-auto mb-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
                      </svg>
                      <div>Failed to load PDF document</div>
                      <div className="text-sm mt-1">{debugInfo}</div>
                    </div>
                  </div>
                }
                className="shadow-lg"
                options={{
                  cMapUrl: 'https://unpkg.com/pdfjs-dist@3.11.174/cmaps/',
                  cMapPacked: true,
                  standardFontDataUrl: 'https://unpkg.com/pdfjs-dist@3.11.174/standard_fonts/',
                }}
              >
                <div className="relative pdf-page-container">
                  <Page
                    pageNumber={pageNumber}
                    scale={scale}
                    renderTextLayer={true}
                    renderAnnotationLayer={false}
                    onLoadSuccess={onPageLoadSuccess}
                    onLoadError={(error) => {
                      console.error(`Page ${pageNumber} load error:`, error);
                      toast.error(`Failed to load page ${pageNumber}: ${error.message}`);
                    }}
                    loading={
                      <div className="flex items-center justify-center p-4">
                        <div className="loading-spinner w-6 h-6"></div>
                        <span className="ml-2">Loading page...</span>
                      </div>
                    }
                    error={
                      <div className="flex items-center justify-center p-4 text-red-600">
                        <span>Failed to load page {pageNumber}</span>
                      </div>
                    }
                    className="pdf-page"
                  />
                  
                  {/* Highlight overlay */}
                  <div className="absolute inset-0 pointer-events-none highlight-overlay">
                    {renderHighlights()}
                  </div>
                  
                  {/* Loading overlay for highlight creation */}
                  {isCreatingHighlight && (
                    <div className="absolute inset-0 bg-white bg-opacity-50 flex items-center justify-center pointer-events-none z-20">
                      <div className="bg-white rounded-lg shadow-lg p-4 flex items-center">
                        <div className="loading-spinner w-4 h-4"></div>
                        <span className="ml-2 text-sm">Creating highlight...</span>
                      </div>
                    </div>
                  )}
                </div>
              </Document>
            ) : (
              <div className="flex items-center justify-center p-8">
                <div className="text-center">
                  <svg className="w-16 h-16 mx-auto text-gray-400 mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                  </svg>
                  <p className="text-gray-500 mb-2">No PDF loaded</p>
                  <p className="text-sm text-gray-400">Debug: {debugInfo}</p>
                  <p className="text-xs text-gray-400">UUID: {uuid}</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Instructions */}
      {isSelecting && pdfUrl && (
        <div className="bg-yellow-50 border-t border-yellow-200 px-4 py-2">
          <p className="text-sm text-yellow-800">
            <strong>Highlighting Mode:</strong> Select text with your mouse to create highlights. 
            Click on existing highlights to delete them.
            {isCreatingHighlight && <span className="ml-2 text-yellow-600">(Creating highlight...)</span>}
          </p>
        </div>
      )}

      {/* Debug info */}
      <div className="bg-gray-100 border-t px-4 py-2 text-xs text-gray-600">
        <div className="flex justify-between">
          <span>Highlights: {highlights.length} | Page: {pageNumber} | Scale: {scale.toFixed(1)}</span>
          <span>PDF: {pdfUrl ? 'Loaded' : 'Not loaded'} | {debugInfo}</span>
        </div>
      </div>
    </div>
    
  );
};

export default PDFViewer;
